import React, { useState } from "react";
import "./home.css";
import axios from "axios";

function App() {
    const [ticker, setTicker] = useState("");
    const [submitted, setSubmitted] = useState(false);


    // Once submitted
    const handleSubmit = (e) => {
        // Set the status to submitted
        e.preventDefault();
        if (ticker.trim() !== "") {
            setSubmitted(true);
        }


        // Give the users name to the backend
        axios
            .post("http://127.0.0.1:5000/api/hello", { ticker })
            // Set the response onto the message
            .then((response) => {
                let backendAnswer = response.data.tickerName;
                console.log(backendAnswer);
            })
            .catch((error) => {
                console.error(error);
            });
    };


    return (
        <>
            <form onSubmit={handleSubmit} className="search-form">
                <input
                    type="text"
                    value={ticker}
                    onChange={(e) => {
                        setTicker(e.target.value);
                        setSubmitted(false); // reset if they start typing again
                    }}
                    placeholder="Enter stock ticker (e.g., AAPL)"
                    className="search-input"
                />
                <button type="submit">Search</button>
            </form>

            {submitted && (
                <div className="results-container">
                    <h2>Results for: {ticker.toUpperCase()}</h2>
                    <div className="sentiment-section">
                        <h3>ðŸ“ˆ Overall Sentiment</h3>
                        <p style={{ color: "green" }}>Positive</p> {/* fake sentiment */}
                    </div>

                    <div className="news-section">
                        <h3>ðŸ“° Related News</h3>
                        <ul>
                            <li>
                                <a href="https://example.com/news1" target="_blank" rel="noopener noreferrer">
                                    Apple beats earnings expectations, stock surges
                                </a>
                            </li>
                            <li>
                                <a href="https://example.com/news2" target="_blank" rel="noopener noreferrer">
                                    Analysts raise price targets for Apple amid strong iPhone demand
                                </a>
                            </li>
                        </ul>
                    </div>
                </div>
            )}
        </>

    );
}

export default App;
