import React, { useState } from "react";
import axios from "axios";
import { Menu } from "lucide-react"; 
import { Link } from "react-router-dom";
import "./home.css";

function App() {
    const [ticker, setTicker] = useState("");
    const [showResults, setShowResults] = useState(false);
    const [sentiment, setSentiment] = useState("");
    const [overallSentiment, setOverallSentiment] = useState("");
    const [news, setNews] = useState([]);
    const [hasSearched, setHasSearched] = useState(false);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    // Function to analyze sentiment of news headlines
    const analyzeSentiment = async (headlines) => {
        try {
            const response = await axios.post("http://127.0.0.1:5000/api/sentiment", {
                headlines: headlines
            });
            return response.data;
        } catch (error) {
            console.error("Sentiment analysis error:", error);
            return [];
        }
    };

    // Function to determine overall sentiment from individual sentiments
    const calculateOverallSentiment = (sentimentResults) => {
        if (!sentimentResults || sentimentResults.length === 0) {
            return "Neutral";
        }

        const counts = { Bullish: 0, Bearish: 0, Neutral: 0 };
        let totalConfidence = 0;

        sentimentResults.forEach(result => {
            counts[result.sentiment]++;
            totalConfidence += result.confidence;
        });

        // Find the sentiment with the highest count
        const maxSentiment = Object.keys(counts).reduce((a, b) => 
            counts[a] > counts[b] ? a : b
        );

        const avgConfidence = (totalConfidence / sentimentResults.length).toFixed(2);

        return `${maxSentiment} (${avgConfidence} confidence)`;
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!ticker.trim()) {
            setError("Please enter a ticker symbol");
            return;
        }

        setLoading(true);
        setError("");
        setHasSearched(true);

        try {
            // Fetch news from backend
            const newsResponse = await axios.get(`http://127.0.0.1:5000/api/stock/${ticker.toUpperCase()}`);
            const newsData = newsResponse.data;

            if (!newsData || newsData.length === 0) {
                setError("No news found for this ticker");
                setLoading(false);
                return;
            }

            // Process news data - get top 5 most recent articles
            const processedNews = newsData
                .slice(0, 5) // Get top 5 articles
                .map(article => ({
                    title: article.headline,
                    url: article.url,
                    source: article.source,
                    publishedAt: new Date(article.datetime * 1000).toLocaleDateString(),
                    summary: article.summary
                }));

            // Extract headlines for sentiment analysis
            const headlines = processedNews.map(article => article.title);

            // Analyze sentiment of headlines
            const sentimentResults = await analyzeSentiment(headlines);

            // Calculate overall sentiment
            const overall = calculateOverallSentiment(sentimentResults);

            // Combine news with sentiment
            const newsWithSentiment = processedNews.map((article, index) => ({
                ...article,
                sentiment: sentimentResults[index]?.sentiment || "Neutral",
                confidence: sentimentResults[index]?.confidence || 0
            }));

            // Update state
            setNews(newsWithSentiment);
            setOverallSentiment(overall);
            setShowResults(true);

        } catch (error) {
            console.error("Error fetching data:", error);
            console.error("Error response:", error.response?.data);
            console.error("Error status:", error.response?.status);

            if (error.response?.status === 404) {
                setError("Ticker not found or no recent news available. Please try a different ticker.");
            } else if (error.response?.status === 500) {
                setError("Server error. Please check if the API key is configured correctly.");
            } else {
                setError(`Failed to fetch data: ${error.response?.data?.error || error.message}`);
            }
        } finally {
            setLoading(false);
        }
    };

    // Get sentiment color for styling
    const getSentimentColor = (sentiment) => {
        if (sentiment.includes('Bullish')) return '#4CAF50'; // Green
        if (sentiment.includes('Bearish')) return '#F44336'; // Red
        return '#FF9800'; // Orange for Neutral
    };

    return (
        <>
            <header className="app-header">
                <div className="logo-area">
                </div>
            </header>

            <main className="content-area">
                <form
                    onSubmit={handleSubmit}
                    className={hasSearched ? "search-form-top" : "search-form"}
                >
                    {!hasSearched && (
                        <div id="titleMessage">
                            <h1 id="quantifyHeading">Quantify</h1>
                            <h2 id="headingDescription">All about the Stock Market</h2>
                        </div>
                    )}

                    {!hasSearched && <h2 id="message">Enter Ticker</h2>}

                    <div id="inputDiv">
                        <input
                            type="text"
                            value={ticker}
                            onChange={(e) => setTicker(e.target.value.toUpperCase())}
                            placeholder="AAPL, GOOG, TSLA..."
                            className="search-input"
                            disabled={loading}
                        />
                        <button type="submit" disabled={loading} className="search-button">
                            {loading ? "Searching..." : "Search"}
                        </button>
                    </div>
                </form>

                {error && (
                    <div className="error-message">
                        <p>{error}</p>
                    </div>
                )}

                {loading && (
                    <div className="loading-container">
                        <p>Loading news and analyzing sentiment...</p>
                    </div>
                )}

                {showResults && !loading && (
                    <div className="results-container">
                        <div className="ticker-header">
                            <h2>{ticker} Analysis</h2>
                        </div>

                        <div className="sentiment-section">
                            <h3>Overall Market Sentiment</h3>
                            <div 
                                className="sentiment-badge"
                                style={{ 
                                    backgroundColor: getSentimentColor(overallSentiment),
                                    color: 'white',
                                    padding: '10px 20px',
                                    borderRadius: '25px',
                                    display: 'inline-block',
                                    fontWeight: 'bold'
                                }}
                            >
                                {overallSentiment}
                            </div>
                        </div>

                        <div className="news-section">
                            <h3>Latest News & Analysis</h3>
                            <div className="news-list">
                                {news.map((article, index) => (
                                    <div key={index} className="news-item">
                                        <div className="news-header">
                                            <a 
                                                href={article.url} 
                                                target="_blank" 
                                                rel="noopener noreferrer"
                                                className="news-title"
                                            >
                                                {article.title}
                                            </a>
                                            <span 
                                                className="article-sentiment"
                                                style={{ color: getSentimentColor(article.sentiment) }}
                                            >
                                                {article.sentiment}
                                            </span>
                                        </div>
                                        <div className="news-meta">
                                            <span className="news-source">{article.source}</span>
                                            <span className="news-date">{article.publishedAt}</span>
                                        </div>
                                        {article.summary && (
                                            <p className="news-summary">{article.summary.substring(0, 150)}...</p>
                                        )}
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                )}
            </main>
        </>
    );
}

export default App;
