import React, { useState } from "react";
import { Routes, Route, Link, useLocation } from "react-router-dom";
import Home from "./pages/Home/home";
import Login from "./pages/Login/login";
import Dashboard from "./pages/Dashboard/dashboard";
import Signup from "./pages/Login/signup";
import { Menu } from "lucide-react";
import "./index.css";
import "./Sidebar.css";

function AppContent() {
    const location = useLocation();
    const [sidebarOpen, setSidebarOpen] = useState(false);

    return (
        <>
            <header className="app-header">
                <div className="logo-area">
                    <Link to="/" id="homePageLogo">Quantify</Link>
                </div>

                {/* Hamburger icon always visible (or conditionally based on auth) */}
                <button
                    onClick={() => setSidebarOpen(true)}
                    className="menu-button"
                    aria-label="Open sidebar"
                >
                    <Menu size={24} />
                </button>

                {location.pathname === "/" && (
                    <div className="auth-area">
                        <Link to="/login" id="login">Login/Signup</Link>
                    </div>
                )}
            </header>

            {/* Sidebar (only when open) */}
            {sidebarOpen && (
                <div className="sidebar">
                    <button className="close-btn" onClick={() => setSidebarOpen(false)}>Ã—</button>
                    <ul>
                        <li>
                            <Link to="/dashboard" onClick={() => setSidebarOpen(false)}>Dashboard</Link>
                        </li>
                    </ul>
                </div>
            )}

            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/login" element={<Login />} />
                <Route path="/signup" element={<Signup />} />
                <Route path="/dashboard" element={<Dashboard />} />
            </Routes>
        </>
    );
}
