import React, { useState } from "react";
import "./login.css";

function Login() {
    const [username, setUsername] = useState("");
    const [usernameGiven, setUsernameGiven] = useState(false);
    const [password, setPassword] = useState("");
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const handleLogin = () => {
        if (usernameGiven === true){
            console.log("BALALALALALALA");
            setIsLoggedIn(true);
        }

    };

    return (
        <div className="login-container">
            {!isLoggedIn ? (
                <>
                    <h1>Login</h1>

                    <div id="loginBubble">

                        <input
                            type="text"
                            placeholder="Username"
                            value={username}
                            onchage={(e) => setUsernameGiven(true)}
                            className="loginBubble"
                        />

                        <input
                            type="text"
                            placeholder="Password"
                            value={password}
                            onchage={(e) => setPassword(e.target.value)}
                            className="loginBubble"
                        />

                        <button onClick={handleLogin}>Login</button>
                    </div>
                </>
            ) : (
                <h2>Welcome, {username}. You're logged in!</h2>
            )}
        </div>
    );
}

export default Login;
